mapper.update:
UPDATE
    SYS_USER
SET
    LAST_LOGIN = #{LAST_LOGIN},
    IP = #{IP}
WHERE USERNAME = #{USERNAME}

mapper.select:
SELECT user.*, ROLE.ROLENAME AS ROLENAME FROM SYS_USER user LEFT JOIN SYS_ROLE role ON user.ROLE_ID = role.ROLE_ID WHERE 1 =1
AND user.USERNAME LIKE CONCAT(CONCAT("%", #{params.USERNAME}), "%")
AND user.NAME LIKE CONCAT(CONCAT("%", #{params.NAME}), "%")
AND user.STATUS = #{params.STATUS}
AND user.ROLE_ID = #{params.ROLE_ID}

mapper.update:
UPDATE
    SYS_USER
SET
    NAME = #{NAME},
    ROLE_ID = #{ROLE_ID},
    PHONE = #{PHONE}

    ,PASSWORD = #{PASSWORD}

WHERE USER_ID = #{USER_ID}
